var dbConn;
var result;
var log_message ='';
var error = false;
var hl7_message_send_message = channelMap.get('hl7_message_send_message')||''
var hl7_message_send_id = channelMap.get('hl7_message_send_id')




if ( $r('SEND FTP') ) {
	if ( $r('SEND FTP').getStatus() == 'ERROR' )
	error = true
}

if ( $r('SEND MLLP') ) {
	if ( $r('SEND MLLP').getStatus() == 'ERROR' )
	error = true
}

	 if ( hl7_message_send_message == '') {
	error = true
}




try {

dbConn = getDBConnection();



	channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();

	var destinationConnectors = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelByName(channelName).getEnabledDestinationConnectors().iterator();
	var json_error = JSON.parse(getChannelError(destinationConnectors, channelName, responseMap, 0));
     if   (json_error.error) {
		error= true
	}

	// suppression du dtail des token  et caractre parasite dans les LOG 
	error_message = json_error.error_message.replace(/\"token\":\"(.*)\"/g, '\"token\":\"OK"');
	error_message = error_message.replace(/'/g, " ");
	log_message = log_message+'\n' + ' HL7_message_send.id : '+  hl7_message_send_id +'\n'+ error_message;



	if(error) {
		error_status=ERROR
		logToFile('ERROR', log_message)
		result = dbConn.executeUpdate('UPDATE hl7_message_send SET status = \'FAILED\' WHERE id = ?', [hl7_message_send_id]);
	} else {
		logToFile('INFO', log_message)
		result = dbConn.executeUpdate('UPDATE hl7_message_send SET status = \'DONE\'   WHERE id = ?', [hl7_message_send_id]);
	}

} finally {
	if (dbConn) {
		dbConn.close();
	}
}


return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;
