step.script:
/* récupère les ID patient pour chaques authorités d'assigantions trouvées dans le segment PDI */

//  Version sans gestion des identifiants multiples  (n'est plus utilisé dans la tabel PATIENT)

/* mapping non valide NON UTILISE cf patientidentifier list
var assigningAuthorityNameSpaceId = null
var identifierTypeCode = 'PI'
var hl7_patient_patient_id =  msg['PID']['PID.3']['PID.3.1'].toString()

if(msg['PID']['PID.3'].length() === 1) {
	hl7_patient_patient_id = msg['PID']['PID.3']['PID.3.1'].toString()
}else{
	for each (var pid in msg['PID']['PID.3']) {
		if(
			(
				assigningAuthorityNameSpaceId == null ||
				pid['PID.3.4'].toString() == assigningAuthorityNameSpaceId				
			)
			&&
			(
				identifierTypeCode == null ||
				pid['PID.3.5'].toString() == identifierTypeCode
			)
		) {
			hl7_patient_patient_id= pid['PID.3.1'].toString()
			break
		}		
	}
}

if(hl7_patient_patient_id === null || hl7_patient_patient_id == '') {
	var message = channelName + ': ' + 'No patient id found in message: ' + $('messageHeader_messageControlId_value')
	logToFile(message, 'WARNING')
	throw 'No patient id found';
}
if(hl7_patient_patient_id.length > 30) {
	var message = channelName + ': ' + 'Patient id too long: ' + $('messageHeader_messageControlId_value')
	logToFile(message)
	throw 'Patient id too long';
}

channelMap.put('patient_hl7_identifier', hl7_patient_patient_id )

*/

