step.script:
// variable à modifier pour parser un numéro de téléphone différent
// l'index commence à 0
var phoneNumberIndex = 0
var privatePhoneNumber = null

/*
code  'PID.13.2']
WPN = téléphone travail
PRN = téléphone fixe domicile
ORN = téléphone mobile 
*/



for (var i = 0; i < msg['PID']['PID.13'].length(); i++) {
		if(msg['PID']['PID.13'][i]['PID.13.2'].toString() == 'ORN'){ //private 

			     privatePhoneNumber = msg['PID']['PID.13'][i]['PID.13.1'].toString().substring(0,16)

		} else if(msg['PID']['PID.13'][i]['PID.13.2'].toString() == 'PH' && privatePhoneNumber == null){

			     privatePhoneNumber = msg['PID']['PID.13'][i]['PID.13.1'].toString().substring(0,16)

		} else if(msg['PID']['PID.13'][i]['PID.13.2'].toString() == '' && privatePhoneNumber == null){

			     privatePhoneNumber = msg['PID']['PID.13'][i]['PID.13.1'].toString().substring(0,16)

		}
	}


