//  Version  avec gestion des identifiants multiples 
// necessite une adaptation dans Uspert Patient et table HlÃ¨_patient_identifier
//PID|||271616^^^EXPERTSANTE^PI~^^^SIGEMS^PI


var identifierList = []

for (var i = 0; i < msg['PID']['PID.3'].length(); i++) {

	// attention il paut y a voir des identifiants   SIGEMS  vides :    PID|||271616^^^EXPERTSANTE^PI~^^^SIGEMS^PI
	// et dans certain cas pas de code PI

	if (msg['PID']['PID.3'][i]['PID.3.1'].length()) {  
		// Gestion du PI-3 , dont l'INS en PAM 2.10
	    		 if ( msg['MSH']['MSH.12']['MSH.12.1'].toString() == "2.5.1" || msg['MSH']['MSH.12']['MSH.12.1'].toString() == "2.5"){
	    		 	if(msg['PID']['PID.3'][i]['PID.3.4']['PID.3.4.2'].length()){
	    		 		if(msg['PID']['PID.3'][i]['PID.3.5'].toString()=='PI' || msg['PID']['PID.3'][i]['PID.3.5'].toString()=='SS'){
	    		 			identifierList.push({
				    		id: 							msg['PID']['PID.3'][i]['PID.3.1'].toString(),
				   		assigning_authority_namespace_id:  msg['PID']['PID.3'][i]['PID.3.4']['PID.3.4.1'].toString(),				    		
				    		oid: 						msg['PID']['PID.3'][i]['PID.3.4']['PID.3.4.2'].toString(),
				    		universal_id_type : 			msg['PID']['PID.3'][i]['PID.3.4']['PID.3.4.3'].toString(),
				    		type_code_id:					msg['PID']['PID.3'][i]['PID.3.5'].toString() //  si vide PI
			    			})
	    		 		}
	    		 		else if(msg['PID']['PID.3'][i]['PID.3.5'].toString()=='INS-NIR'
			    		 		|| msg['PID']['PID.3'][i]['PID.3.5'].toString()=='INS-NIA' 
			    		 		|| msg['PID']['PID.3'][i]['PID.3.5'].toString()=='INS'){

	    		 				if (msg['PID']['PID.32'][0]['PID.32.1'].toString() == 'VALI'){// Pour traiter l'INS, le PID-32 doit valoir VALI, sinon on ignore l'INS
		    		 				identifierList.push({
							    		id: 							msg['PID']['PID.3'][i]['PID.3.1'].toString(),
							   		assigning_authority_namespace_id:  msg['PID']['PID.3'][i]['PID.3.4']['PID.3.4.1'].toString(),				    		
							    		oid: 						msg['PID']['PID.3'][i]['PID.3.4']['PID.3.4.2'].toString(),
							    		universal_id_type : 			msg['PID']['PID.3'][i]['PID.3.4']['PID.3.4.3'].toString(),
							    		type_code_id:					msg['PID']['PID.3'][i]['PID.3.5'].toString() //  si vide PI
						    		})
		    		 			}
		    		 			else {
		    		 				// On ne fait rien
		    		 			}
	    		 		}

	    		 	}
	    		 	else{
	    		 		identifierList.push({
				    		id: 							msg['PID']['PID.3'][i]['PID.3.1'].toString(),
				   		assigning_authority_namespace_id:  msg['PID']['PID.3'][i]['PID.3.4'].toString(),//||'XPLORE',
				    		oid: 						"",
				    		universal_id_type : 			"",
				    		type_code_id:					"PI"
				    })

	    		 	}
	    		 }
    		 	else{
    		 		if(msg['PID']['PID.3'][i]['PID.3.5'].toString()=='INS-NIR'
			    		 		|| msg['PID']['PID.3'][i]['PID.3.5'].toString()=='INS-NIA' 
			    		 		|| msg['PID']['PID.3'][i]['PID.3.5'].toString()=='INS'){
			    		 		// On ne fait rien		
			    		 		}
			    	else{
	    		 		identifierList.push({
				    		id: 							msg['PID']['PID.3'][i]['PID.3.1'].toString(),
				   		assigning_authority_namespace_id:  msg['PID']['PID.3'][i]['PID.3.4']['PID.3.4.1'].toString()|| msg['PID']['PID.3'][i]['PID.3.4'].toString(),//||'XPLORE',
				    		oid: 						"",
				    		universal_id_type : 			"",
				    		type_code_id:					"PI"
			   		 })
			    	}
    		 	}
	    	} 	
 	}


 channelMap.put('patient_identifierList', identifierList);

