step.script:
// prendre le  nom marital (celui prÃ©sent sur la carte vitale )  Type D

var last_name_D = ''
var first_name_D = ''

var last_name_L = ''
var first_name_L = ''



//  Dans toutes les occurences de noms, on fait le trie entre les nom  de type D et de ytype L 

for each (var patientNameId in msg['PID']['PID.5']) {

	if(patientNameId['PID.5.7'].toString() == 'L') {
		last_name_L = patientNameId['PID.5.1'].toString().toUpperCase().trim().replace(/'/g, " ")
		first_name_L = patientNameId['PID.5.2'].toString().toUpperCase().trim().replace(/'/g, " ")
       } else if ( patientNameId['PID.5.7'].toString() == 'D') {
		last_name_D = patientNameId['PID.5.1'].toString().toUpperCase().trim().replace(/'/g, " ")
		first_name_D = patientNameId['PID.5.2'].toString().toUpperCase().trim().replace(/'/g, " ")
	  } else   {
		last_name_L = patientNameId['PID.5.1'].toString().toUpperCase().trim().replace(/'/g, " ")
		first_name_L = patientNameId['PID.5.2'].toString().toUpperCase().trim().replace(/'/g, " ")
	}
}

 // si aucun type D trouvÃ© dans toutes les occurences
 // alors prendre le type L 

if(last_name_D == '' ) {
	channelMap.put('patient_last_name', last_name_L )
	channelMap.put('patient_first_name', first_name_L )
} else {
	channelMap.put('patient_last_name', last_name_D )
	channelMap.put('patient_first_name', first_name_D )
}

