// variable Ã  modifier pour parser un numÃ©ro de tÃ©lÃ©phone diffÃ©rent
// l'index commence Ã  0
var phoneNumberIndex = 0
var privatePhoneNumber = null

/*
code  'PID.13.2']
WPN = tÃ©lÃ©phone travail
PRN = tÃ©lÃ©phone fixe domicile
ORN = tÃ©lÃ©phone mobile 
*/



for (var i = 0; i < msg['PID']['PID.13'].length(); i++) {
		if(msg['PID']['PID.13'][i]['PID.13.2'].toString() == 'ORN'){ //private 

