					assigningAuthority: 'EDL',
					identifiantTypeCode: 'CR' ,
					documentDate :    document_date  || DateUtil.getCurrentDate("yyyyMMddHHmmss"),   
					source : 	               sourceDocument
				})
	    }

     }
}

channelMap.put('documents', documents)

entry.string:
var documents = []

var study_AccNum_ToLink = null 
var  link_level = null 

var document_date =null
var sourceDocument = 'ris' 

if ( msg['MSH']['MSH.9']['MSH.9.1'].toString() == 'SIU' ) {
	study_AccNum_ToLink =   msg['SCH']['SCH.27']['SCH.27.1'].toString() || msg['SCH']['SCH.2']['SCH.2.1'].toString() 
	document_date= dateParser(msg['SCH']['SCH.11']['SCH.11.4'].toString())  

}

if ( msg['MSH']['MSH.9']['MSH.9.1'].toString() == 'ORM'  ) {
    study_AccNum_ToLink=   msg['OBR']['OBR.3']['OBR.3.1'].toString() || msg['SCH']['SCH.2']['SCH.2.1'].toString() 
    document_date= dateParser(msg['OBR']['OBR.7']['OBR.7.1'].toString())  

}

if ( msg['MSH']['MSH.9']['MSH.9.1'].toString() == 'ORU' ) {
	study_AccNum_ToLink =   msg['OBR']['OBR.3']['OBR.3.1'].toString() || msg['SCH']['SCH.2']['SCH.2.1'].toString() 
	document_date= dateParser(msg['OBR']['OBR.7']['OBR.7.1'].toString())  
}



if (study_AccNum_ToLink== null || study_AccNum_ToLink == '' ) {
	link_level ='PATIENT'
	}else {
	link_level ='STUDY'
}




if(msg['OBX'].toString().length > 0) {
	for (var i = 0; i < msg['OBX'].length(); i++) {
		if(msg['OBX'][i]['OBX.2']['OBX.2.1'].toString()== 'ED') {   
				documents.push({
					documentTypeCode : 		msg['OBX'][i]['OBX.3']['OBX.3.1'].toString(),
					documentTypeLibelle :	'COMPTE RENDU' ,   // msg['OBX'][i]['OBX.3']['OBX.3.2'].toString(),
					documentContent :		msg['OBX'][i]['OBX.5']['OBX.5.5'].toString(),
					targetFormat :			msg['OBX'][i]['OBX.5']['OBX.5.3'].toString(),
					encodedFormat  :		msg['OBX'][i]['OBX.5']['OBX.5.4'].toString(),
					studyAccNumToLink:		study_AccNum_ToLink,
					linkLevel :   	   		link_level,
					externalId: msg['OBR']['OBR.20']['OBR.20.1'].toString(),
					assigningAuthority: 'EDL',
					identifiantTypeCode: 'CR' ,
					documentDate :    document_date  || DateUtil.getCurrentDate("yyyyMMddHHmmss"),   
					source : 	               sourceDocument
				})
	    }

     }
}

