step.script:
//  Permet de dÃ©clencher le ReplaceStudies  et les nexStep qui vont transfomer les rendez-vous en examens, changer le patient de liste d'attente 
// admission du patient, examen termnÃ©, patient sorti

//  les conditions sur les ORC sont spÃ©cifiques Ã  chaques Ã©diteur 

var nextStep = null


// -------------------  ORM : Studies  EDL EXPLORE-------------------	

if (msg['MSH']['MSH.9']['MSH.9.1'].toString() == 'ORM' ){


	// annulation de l'examen
	if (  msg['ORC']['ORC.1']['ORC.1.1'].toString() == 'CA' || 
           msg['ORC']['ORC.5']['ORC.5.1'].toString() == 'CA' )  {
              nextStep = 'CANCELED'
      }

  	// examens terminÃ©s
     if (msg['ORC']['ORC.5']['ORC.5.1'].toString() == 'CM' )  {
              nextStep = 'STUDY_DONE'
      }

	// admission RIS

     if (  msg['ORC']['ORC.1']['ORC.1.1'].toString() == 'NW' || 
           msg['ORC']['ORC.1']['ORC.1.1'].toString() == 'SC' || 
           msg['ORC']['ORC.1']['ORC.1.1'].toString() == 'XO'    )  {

  		 if (msg['ORC']['ORC.5']['ORC.5.1'].toString() == 'IP' )  {
              nextStep = 'ADMITTED'
              admit_date_time = dateParser(msg['ORC']['ORC.7']['ORC.7.4'].toString())
           }

     } 


 } 


// admission canceled
if (msg['MSH']['MSH.9']['MSH.9.1'].toString() == 'ADT' && 
    msg['MSH']['MSH.9']['MSH.9.2'].toString() == 'A11'      ) {
       nextStep = 'ADMISSION_CANCELED'
    }

channelMap.put('nextStep', nextStep );

